mapper.sql:
FROM 
	infrMember a
	LEFT JOIN infrMemberEmail b ON b.ifmeDelny = 0 AND b.ifmeDefaultNy = 1 AND b.ifmmSeq = a.ifmmSeq
	LEFT JOIN infrMemberPhone c ON c.ifmpDelny = 0 AND c.ifmpDefaultNy = 1 AND c.ifmmSeq = a.ifmmSeq
WHERE 1=1
	AND ifmmDelNy = #{shDelNy}

		AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.ifmmDob BETWEEN #{shDateStart} AND #{shDateEnd}


		AND ifmmName LIKE CONCAT('%',#{shValue},'%')
		AND ifmmId LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrMember (
	ifmmAdminNy
	,ifmmDormancyNy
	,ifmmName
	,ifmmNameEng
	,ifmmLastName
	,ifmmFirstName
	,ifmmLastNameEng
	,ifmmFirstNameEng
	,ifmmId
	,ifmmPassword
	,ifmmNickname
	,ifmmGenderCd
	,ifmmDob
	,ifmmSavedCd
	,ifmmMarriageCd
	,ifmmChildrenNum
	,ifmmFavoriteColor
	,ifmmRecommenderSeq
	,ifmmEmailConsentNy
	,ifmmSmsConsentNy
	,ifmmPushConsentNy
	,ifmmDesc
	,ifmmDelNy

)
VALUES (
	1
	,0
	,#{ifmmName}
	,#{ifmmNameEng}
	,#{ifmmLastName}
	,#{ifmmFirstName}
	,#{ifmmLastNameEng}
	,#{ifmmFirstNameEng}
	,#{ifmmId}
	,#{ifmmPassword}
	,#{ifmmNickname}
	,#{ifmmGenderCd}

		,NULL
		,#{ifmmDob}

	,#{ifmmSavedCd}
	,#{ifmmMarriageCd}
	,#{ifmmChildrenNum}
	,#{ifmmFavoriteColor}
	,#{ifmmRecommenderSeq}
	,#{ifmmEmailConsentNy}
	,#{ifmmSmsConsentNy}
	,#{ifmmPushConsentNy}
	,#{ifmmDesc}
	,0

)

	SELECT last_insert_id()

mapper.update:
UPDATE
	infrMember
SET
	ifmmAdminNy = #{ifmmAdminNy}
	,ifmmDormancyNy = #{ifmmDormancyNy}
	,ifmmName = #{ifmmName}
	,ifmmNameEng = #{ifmmNameEng}
	,ifmmLastName = #{ifmmLastName}
	,ifmmFirstName = #{ifmmFirstName}
	,ifmmLastNameEng = #{ifmmLastNameEng}
	,ifmmFirstNameEng = #{ifmmFirstNameEng}
	,ifmmNickname = #{ifmmNickname}
	,ifmmGenderCd = #{ifmmGenderCd}
	,ifmmDob = #{ifmmDob}
	,ifmmSavedCd = #{ifmmSavedCd}
	,ifmmMarriageCd = #{ifmmMarriageCd}
	,ifmmMarriageDate = #{ifmmMarriageDate}
	,ifmmChildrenNum = #{ifmmChildrenNum}
	,ifmmFavoriteColor = #{ifmmFavoriteColor}
	,ifmmRecommenderSeq = #{ifmmRecommenderSeq}
	,ifmmEmailConsentNy = #{ifmmEmailConsentNy}
	,ifmmSmsConsentNy = #{ifmmSmsConsentNy}
	,ifmmPushConsentNy = #{ifmmPushConsentNy}
	,ifmmDesc = #{ifmmDesc}

WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
